package com.sun.faces.test.servlet30.client_encrypt_by_default;

import static com.sun.faces.test.junit.JsfServerExclude.WEBLOGIC_12_2_1;
import static com.sun.faces.test.junit.JsfVersion.JSF_2_3_0_M07;
import static org.junit.Assert.assertTrue;

import org.junit.After;
import org.junit.Before;
import org.junit.Test;
import org.junit.runner.RunWith;

import com.gargoylesoftware.htmlunit.WebClient;
import com.gargoylesoftware.htmlunit.html.HtmlHiddenInput;
import com.gargoylesoftware.htmlunit.html.HtmlPage;
import com.gargoylesoftware.htmlunit.html.HtmlSubmitInput;
import com.gargoylesoftware.htmlunit.html.HtmlTextInput;
import com.sun.faces.test.junit.JsfTest;
import com.sun.faces.test.junit.JsfTestRunner;

/**
 * Verify a simple facelet page.
 *
 */
@RunWith(JsfTestRunner.class)
public class ClientEncryptByDefaultIT {

    /**
     * Stores the web URL.
     */
    private String webUrl;

    /**
     * Stores the web client.
     */
    private WebClient webClient;


    /**
     * Setup before testing.
     */
    @Before
    public void setUp() {
        webUrl = System.getProperty("integration.url");
        webClient = new WebClient();
    }

    /**
     * Tear down after testing.
     */
    @After
    public void tearDown() {
        webClient.close();
    }

    /*
     * From: Jake Evans Here are the steps to reproduce the issue we're seeing.
     *
     * Steps to build payload: 1. download ysoserial-0.0.3-all.jar 2. create payload: java -jar
     * ysoserial-0.0.3-all.jar CommonsCollections1 'touch /tmp/ClientStateSavingPasswordIneffective.txt'
     * > payload.out 3. gzip payload: gzip -f payload.out 4. base64-encode payload: base64 -w 0
     * payload.out.gz > payload.out.b64
     */
    public static final String touchTmpClientStateSavingPasswordIneffectiveTxt = "";

    @JsfTest(value = JSF_2_3_0_M07, excludes = { WEBLOGIC_12_2_1 })
    @Test
    public void testClientStateEncrypted() throws Exception {
        HtmlPage page = webClient.getPage(webUrl + "/greeting.xhtml");

        webClient.getOptions().setThrowExceptionOnFailingStatusCode(false);

        HtmlHiddenInput stateField = (HtmlHiddenInput) page.getHtmlElementById("j_id1:javax.faces.ViewState:0");
        stateField.setValueAttribute(touchTmpClientStateSavingPasswordIneffectiveTxt);
        HtmlTextInput textField = (HtmlTextInput) page.getHtmlElementById("userNo");
        textField.setValueAttribute("5");

        HtmlSubmitInput button = (HtmlSubmitInput) page.getHtmlElementById("submit");

        page = (HtmlPage) button.click();

        assertTrue(page.asText().indexOf("Guess The Number") == -1);
    }
}
